import React from "react";

// Define the System interface
interface System {
  _id: string;
  type: string; // The type of system (workflow, vendorSolution, vehicle)
  workflowItems?: string[]; // Workflow items (if applicable)
  vendorType?: string; // Vendor type (if applicable)
  vendorName?: string; // Vendor name (if applicable)
  year?: number; // Vehicle year (if applicable)
  make?: string; // Vehicle make (if applicable)
  model?: string; // Vehicle model (if applicable)
}

// Props for the SystemTable component
interface SystemTableProps {
  systems: System[]; // Array of systems to display
  onDeleteSystem: (systemId: string) => void; // Callback for deleting a system
  onEditSystem: (systemId: string) => void; // Callback for editing a system
}

const SystemTable: React.FC<SystemTableProps> = ({
  systems,
  onDeleteSystem,
  onEditSystem,
}) => {
  return (
    <table className="min-w-full table-auto bg-white shadow-lg">
      <thead>
        <tr>
          <th className="px-4 py-2">Type</th>
          <th className="px-4 py-2">Details</th>
          <th className="px-4 py-2">Actions</th>
        </tr>
      </thead>
      <tbody>
        {systems.map((system) => (
          <tr key={system._id}>
            <td className="border px-4 py-2">{system.type}</td>
            <td className="border px-4 py-2">
              {/* Keep table layout consistent regardless of system type */}
              <ul className="grid grid-cols-3 gap-4">
                <li>
                  {system.type === "workflow" ? (
                    <ul>
                      {system.workflowItems?.map((item, idx) => (
                        <li key={idx}>{item}</li>
                      ))}
                    </ul>
                  ) : system.type === "vendorSolution" ? (
                    <>
                      <p>Vendor: {system.vendorName}</p>
                    </>
                  ) : system.type === "vehicle" ? (
                    <>
                      <p>Year: {system.year}</p>
                      <p>Make: {system.make}</p>
                      <p>Model: {system.model}</p>
                    </>
                  ) : null}
                </li>
              </ul>
            </td>
            <td className="border px-4 py-2">
              <button
                onClick={() => onEditSystem(system._id)}
                className="bg-yellow-500 text-white px-4 py-1 rounded mr-2 hover:bg-yellow-600"
              >
                Edit
              </button>
              <button
                onClick={() => onDeleteSystem(system._id)}
                className="bg-red-500 text-white px-4 py-1 rounded hover:bg-red-600"
              >
                Delete
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default SystemTable;
