// OrganizationList.tsx

import React from "react";
import API_BASE_URL from "../config"; // Import the base URL for API calls

// Define the structure of an Organization object
interface Organization {
  _id: string; // Organization ID (MongoDB ObjectId)
  name: string; // Organization name
}

// Define the props expected by the OrganizationList component
interface OrganizationListProps {
  organizations: Organization[]; // Array of organizations to display
  onSelect: (orgId: string, orgName: string) => void; // Function to handle organization selection
  onUpdate: (orgId: string) => void; // Function to handle organization update
  onDelete: (orgId: string) => void; // Function to handle organization deletion
}

const OrganizationList: React.FC<OrganizationListProps> = ({
  organizations,
  onSelect,
  onUpdate,
  onDelete, // Keep this prop for refreshing after deletion
}) => {
  // Function to handle updating the organization name
  const handleEdit = (orgId: string, currentName: string) => {
    const newName = prompt(
      "Enter a new name for the organization:",
      currentName
    );

    if (newName && newName !== currentName) {
      onUpdate(orgId, newName); // Pass the new name directly to App.tsx
    }
  };

  // Function to handle deleting an organization
  const handleDelete = (orgId: string, orgName: string) => {
    const confirmed = window.confirm(
      `Are you sure you want to delete the organization "${orgName}"? This will also delete all associated systems.`
    );

    if (confirmed) {
      console.log("Triggering delete for organization with ID:", orgId);
      onDelete(orgId); // Only trigger the parent deletion function
    }
  };

  return (
    <ul className="space-y-2">
      {organizations.map((org) => (
        <li key={org._id} className="flex justify-between items-center py-2">
          {/* Organization name - clickable to select the organization */}
          <span
            onClick={() => onSelect(org._id, org.name)}
            className="cursor-pointer text-lg text-blue-600"
          >
            {org.name}
          </span>

          {/* Edit and Delete buttons */}
          <div>
            <button
              onClick={() => handleEdit(org._id, org.name)} // Call the handleEdit function for editing
              className="text-blue-500 mx-2"
            >
              Edit
            </button>
            <button
              onClick={() => handleDelete(org._id, org.name)} // Call the handleDelete function for deleting
              className="text-red-500 mx-2"
            >
              Delete
            </button>
          </div>
        </li>
      ))}
    </ul>
  );
};

export default OrganizationList;
