import React, { useState } from "react";
import API_BASE_URL from "../config";

const systemTypes = ["Workflow", "Vendor", "Vehicle"];

interface SystemFormProps {
  orgId: string;
  onSystemAdded: () => void;
}

const SystemForm: React.FC<SystemFormProps> = ({ orgId, onSystemAdded }) => {
  const [selectedType, setSelectedType] = useState(systemTypes[0]);
  const [workflowName, setWorkflowName] = useState<string>("");
  const [workflow, setWorkflow] = useState<string>("");
  const [vendorName, setVendorName] = useState<string>("");
  const [vendorClassification, setVendorClassification] = useState<string>("");
  const [vehicleInfo, setVehicleInfo] = useState({
    year: "",
    make: "",
    model: "",
  });
  const [error, setError] = useState<string | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false); // Track submission state

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setIsSubmitting(true); // Disable the form to prevent multiple submissions

    let systemDetails;
    if (selectedType === "Workflow") {
      systemDetails = { name: workflowName, workflow };
    } else if (selectedType === "Vendor") {
      systemDetails = {
        name: vendorName,
        classification: vendorClassification,
      };
    } else if (selectedType === "Vehicle") {
      if (!vehicleInfo.year || !vehicleInfo.make || !vehicleInfo.model) {
        setError("Vehicle details are incomplete.");
        setIsSubmitting(false);
        return;
      }
      systemDetails = {
        year: vehicleInfo.year,
        make: vehicleInfo.make,
        model: vehicleInfo.model,
      };
    }

    try {
      const response = await fetch(
        `${API_BASE_URL}/api/organizations/${orgId}/systems`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            type: selectedType.toLowerCase() + "System",
            ...systemDetails,
          }),
        }
      );

      if (!response.ok) {
        throw new Error("Failed to add system");
      }

      // Clear form fields on success
      setWorkflowName("");
      setWorkflow("");
      setVendorName("");
      setVendorClassification("");
      setVehicleInfo({ year: "", make: "", model: "" });
      onSystemAdded();
    } catch (err: unknown) {
      setError(`Error adding system: ${(err as Error).message}`);
    } finally {
      setIsSubmitting(false); // Re-enable form submission
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="p-6 bg-gray-100 rounded-lg shadow-md"
    >
      <h2 className="text-xl font-semibold mb-4">Add System</h2>
      <label className="block mb-2">System Type</label>
      <select
        value={selectedType}
        onChange={(e) => setSelectedType(e.target.value)}
        className="w-full mb-4 p-2 border rounded"
        disabled={isSubmitting}
      >
        {systemTypes.map((type) => (
          <option key={type} value={type}>
            {type}
          </option>
        ))}
      </select>

      {/* Form fields for each system type */}
      <div>
        {selectedType === "Workflow" && (
          <>
            <div className="mb-4">
              <label>Workflow Name</label>
              <input
                type="text"
                value={workflowName}
                onChange={(e) => setWorkflowName(e.target.value)}
                className="w-full p-2 border rounded"
                placeholder="Enter workflow name"
                disabled={isSubmitting}
              />
            </div>
            <div className="mb-4">
              <label>Workflow</label>
              <textarea
                value={workflow}
                onChange={(e) => setWorkflow(e.target.value)}
                className="w-full p-2 border rounded"
                placeholder="Enter workflow description"
                disabled={isSubmitting}
              />
            </div>
          </>
        )}

        {selectedType === "Vendor" && (
          <>
            <div className="mb-4">
              <label>Vendor Name</label>
              <input
                type="text"
                value={vendorName}
                onChange={(e) => setVendorName(e.target.value)}
                className="w-full p-2 border rounded"
                placeholder="Enter vendor name"
                disabled={isSubmitting}
              />
            </div>
            <div className="mb-4">
              <label>Vendor Classification</label>
              <input
                type="text"
                value={vendorClassification}
                onChange={(e) => setVendorClassification(e.target.value)}
                className="w-full p-2 border rounded"
                placeholder="Enter vendor classification"
                disabled={isSubmitting}
              />
            </div>
          </>
        )}

        {selectedType === "Vehicle" && (
          <>
            <div className="mb-4">
              <label>Vehicle Year</label>
              <input
                type="text"
                value={vehicleInfo.year}
                onChange={(e) =>
                  setVehicleInfo({ ...vehicleInfo, year: e.target.value })
                }
                className="w-full p-2 border rounded"
                placeholder="Enter vehicle year"
                disabled={isSubmitting}
              />
            </div>
            <div className="mb-4">
              <label>Vehicle Make</label>
              <input
                type="text"
                value={vehicleInfo.make}
                onChange={(e) =>
                  setVehicleInfo({ ...vehicleInfo, make: e.target.value })
                }
                className="w-full p-2 border rounded"
                placeholder="Enter vehicle make"
                disabled={isSubmitting}
              />
            </div>
            <div className="mb-4">
              <label>Vehicle Model</label>
              <input
                type="text"
                value={vehicleInfo.model}
                onChange={(e) =>
                  setVehicleInfo({ ...vehicleInfo, model: e.target.value })
                }
                className="w-full p-2 border rounded"
                placeholder="Enter vehicle model"
                disabled={isSubmitting}
              />
            </div>
          </>
        )}
      </div>

      {/* Display any error messages */}
      {error && <p className="text-red-500 mb-4">{error}</p>}

      {/* Submit button, disabled during form submission */}
      <button
        type="submit"
        className="w-full bg-blue-600 text-white p-2 rounded-lg"
        disabled={isSubmitting}
      >
        {isSubmitting ? "Adding..." : "Add System"}
      </button>
    </form>
  );
};

export default SystemForm;
