// client/src/components/OrganizationForm.tsx

import React, { useState } from "react"; // Import React and the useState hook
import API_BASE_URL from "../config"; // Import the base URL for API requests

// Define the props interface for OrganizationForm
interface OrganizationFormProps {
  onOrganizationCreated: (orgId: string, orgName: string) => void; // Prop passed to handle organization creation in the parent component
}

// Main functional component for creating a new organization
const OrganizationForm: React.FC<OrganizationFormProps> = ({
  onOrganizationCreated,
}) => {
  // State to track the organization name and any potential errors
  const [name, setName] = useState(""); // State to store the organization name
  const [error, setError] = useState<string | null>(null); // State to store any error messages

  // Function to handle form submission
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault(); // Prevent the default form submission behavior (page reload)
    setError(null); // Clear any existing error state before new submission

    try {
      // Make POST request to create a new organization
      const response = await fetch(`${API_BASE_URL}/api/organizations`, {
        method: "POST",
        headers: { "Content-Type": "application/json" }, // Define request content type as JSON
        body: JSON.stringify({ name }), // Send the organization name as the request body
      });

      if (!response.ok) {
        // If the response status is not OK, throw an error
        throw new Error("Failed to create organization");
      }

      // Parse the response data
      const data = await response.json();

      // Call the parent component's handler to notify about the new organization
      onOrganizationCreated(data._id, data.name);

      // Reset the form input after successful creation
      setName("");
    } catch (err: unknown) {
      // Catch any errors and set the error state with the error message
      setError(`Error creating organization: ${(err as Error).message}`);
    }
  };

  return (
    <form
      onSubmit={handleSubmit} // Attach the submit handler to the form
      className="mt-4 p-4 bg-gray-50 rounded-lg shadow-lg"
    >
      <div className="mb-4">
        {/* Input field for the organization name */}
        <input
          type="text"
          value={name} // Bind the input value to the name state
          onChange={(e) => setName(e.target.value)} // Update the name state when the user types
          className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-500"
          placeholder="Enter organization name" // Input placeholder
          required // Make the input field required
        />
      </div>

      {/* Display an error message if there's an error */}
      {error && <p className="text-red-500 mb-4">{error}</p>}

      {/* Submit button for creating the organization */}
      <button
        type="submit"
        className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50"
      >
        Create Organization
      </button>
    </form>
  );
};

export default OrganizationForm;
