import React, { useState } from "react"; // Import React and useState for state management
import API_BASE_URL from "../config"; // Import the base URL for API requests

interface OrganizationFormProps {
  onOrganizationCreated: (orgId: string, orgName: string) => void; // A callback prop for when a new organization is created
}

const OrganizationForm: React.FC<OrganizationFormProps> = ({
  onOrganizationCreated,
}) => {
  const [name, setName] = useState(""); // State to store the organization's name
  const [error, setError] = useState<string | null>(null); // Track errors
  const [isSubmitting, setIsSubmitting] = useState(false); // Track form submission state

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault(); // Prevent default form behavior (page reload)
    setError(null); // Reset the error state
    setIsSubmitting(true); // Disable the form to prevent multiple submissions

    try {
      const response = await fetch(`${API_BASE_URL}/api/organizations`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name }), // Send the organization name in the request body
      });

      if (!response.ok) {
        throw new Error("Failed to create organization");
      }

      const data = await response.json();
      onOrganizationCreated(data._id, data.name); // Notify parent of new organization
      setName(""); // Clear form input
    } catch (err: unknown) {
      setError(`Error creating organization: ${(err as Error).message}`);
    } finally {
      setIsSubmitting(false); // Re-enable form submission
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="mt-4 p-4 bg-neutral rounded-lg shadow-lg"
    >
      <div className="mb-4">
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="w-full p-2 border border-gray-300 rounded-lg"
          placeholder="Enter organization name"
          required
          disabled={isSubmitting} // Disable input when submitting
        />
      </div>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <button
        type="submit"
        className="w-full bg-primary text-white py-2 rounded-lg hover:bg-accent"
        disabled={isSubmitting} // Disable button when submitting
      >
        {isSubmitting ? "Creating..." : "Create Organization"}
      </button>
    </form>
  );
};

export default OrganizationForm;
