import React from "react";

// Define the System interface to represent a system object
interface System {
  _id: string;
  type: string; // The type of system (workflow, vendorSolution, vehicle)
  workflowItems?: string[]; // Array of workflow items (if the system is a workflow)
  vendorType?: string; // Vendor type (if the system is a vendor solution)
  vendorName?: string; // Vendor name (if the system is a vendor solution)
  year?: number; // Year of the vehicle (if the system is a vehicle)
  make?: string; // Make of the vehicle (if the system is a vehicle)
  model?: string; // Model of the vehicle (if the system is a vehicle)
}

// Props for the SystemTable component
interface SystemTableProps {
  systems: System[]; // Array of systems to display in the table
  onDeleteSystem: (systemId: string) => void; // Function for deleting a system
  onEditSystem: (systemId: string) => void; // Function for editing a system
}

// Main component for displaying the systems in a table format
const SystemTable: React.FC<SystemTableProps> = ({
  systems,
  onDeleteSystem,
  onEditSystem,
}) => {
  return (
    <table className="min-w-full table-auto bg-white shadow-lg">
      <thead>
        <tr>
          <th className="px-4 py-2 w-1/6">System Type</th>{" "}
          {/* Fixed column width */}
          <th className="px-4 py-2 w-1/6">Workflow Items</th>{" "}
          {/* New Workflow column */}
          <th className="px-4 py-2 w-1/6">Vendor Name</th>{" "}
          {/* New Vendor Name column */}
          <th className="px-4 py-2 w-1/6">Vehicle Year</th>{" "}
          {/* New Vehicle Year */}
          <th className="px-4 py-2 w-1/6">Vehicle Make</th>{" "}
          {/* New Vehicle Make */}
          <th className="px-4 py-2 w-1/6">Vehicle Model</th>{" "}
          {/* New Vehicle Model */}
          <th className="px-4 py-2 w-1/6">Actions</th> {/* Action buttons */}
        </tr>
      </thead>
      <tbody>
        {systems.map((system) => (
          <tr key={system._id}>
            <td className="border px-4 py-2">{system.type}</td>

            {/* Workflow Items column */}
            <td className="border px-4 py-2">
              {system.type === "workflow" && (
                <ul>
                  {system.workflowItems?.map((item, index) => (
                    <li key={index}>{item}</li> // Display workflow items
                  ))}
                </ul>
              )}
            </td>

            {/* Vendor Name column */}
            <td className="border px-4 py-2">
              {system.type === "vendorSolution" && (
                <p>{system.vendorName}</p> // Display vendor name for vendor solutions
              )}
            </td>

            {/* Vehicle Year column */}
            <td className="border px-4 py-2">
              {system.type === "vehicle" && (
                <p>{system.year}</p> // Display vehicle year
              )}
            </td>

            {/* Vehicle Make column */}
            <td className="border px-4 py-2">
              {system.type === "vehicle" && (
                <p>{system.make}</p> // Display vehicle make
              )}
            </td>

            {/* Vehicle Model column */}
            <td className="border px-4 py-2">
              {system.type === "vehicle" && (
                <p>{system.model}</p> // Display vehicle model
              )}
            </td>

            {/* Actions column */}
            <td className="border px-4 py-2">
              <button
                onClick={() => onEditSystem(system._id)}
                className="bg-yellow-500 text-white px-4 py-1 rounded mr-2 hover:bg-yellow-600"
              >
                Edit
              </button>
              <button
                onClick={() => onDeleteSystem(system._id)}
                className="bg-red-500 text-white px-4 py-1 rounded hover:bg-red-600"
              >
                Delete
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default SystemTable;
