import React, { useState } from "react";
import API_BASE_URL from "../config";

interface SystemFormProps {
  orgId: string;
  onSystemAdded: () => void;
}

const SystemForm: React.FC<SystemFormProps> = ({ orgId, onSystemAdded }) => {
  const [type, setType] = useState("workflow"); // Default system type
  const [workflowItems, setWorkflowItems] = useState<string[]>([]); // Workflow items
  const [newWorkflowItem, setNewWorkflowItem] = useState(""); // For adding workflow items

  const [vendorType, setVendorType] = useState(""); // For Vendor Solution
  const [vendorName, setVendorName] = useState(""); // For Vendor Solution

  const [vehicleYear, setVehicleYear] = useState(""); // For Vehicle
  const [vehicleMake, setVehicleMake] = useState(""); // For Vehicle
  const [vehicleModel, setVehicleModel] = useState(""); // For Vehicle

  const [error, setError] = useState<string | null>(null);

  // Handle form submission
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);

    // Prepare the system data based on the selected type
    let systemData: any = { type };

    if (type === "workflow") {
      systemData.workflowItems = workflowItems;
    } else if (type === "vendorSolution") {
      systemData.vendorType = vendorType;
      systemData.vendorName = vendorName;
    } else if (type === "vehicle") {
      systemData.year = vehicleYear;
      systemData.make = vehicleMake;
      systemData.model = vehicleModel;
    }

    try {
      const response = await fetch(
        `${API_BASE_URL}/api/organizations/${orgId}/systems`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(systemData),
        }
      );

      if (!response.ok) {
        throw new Error("Failed to add system");
      }

      // Clear form after submission
      setWorkflowItems([]);
      setVendorType("");
      setVendorName("");
      setVehicleYear("");
      setVehicleMake("");
      setVehicleModel("");

      onSystemAdded(); // Notify parent component
    } catch (err: unknown) {
      setError(`Error adding system: ${(err as Error).message}`);
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="mt-4 p-4 bg-gray-50 rounded-lg shadow-lg"
    >
      <h2 className="text-lg font-semibold text-gray-800 mb-4">Add System</h2>

      {/* Dropdown for selecting system type */}
      <div className="mb-4">
        <label className="block text-gray-700 mb-2">System Type</label>
        <select
          value={type}
          onChange={(e) => setType(e.target.value)}
          className="w-full p-2 border border-gray-300 rounded-lg"
        >
          <option value="workflow">Workflow</option>
          <option value="vendorSolution">Vendor Solution</option>
          <option value="vehicle">Vehicle</option>
        </select>
      </div>

      {/* Conditional Fields Based on Type */}
      {type === "workflow" && (
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Workflow Items</label>
          <div>
            {workflowItems.map((item, index) => (
              <p key={index} className="text-gray-600">
                {item}
              </p>
            ))}
          </div>
          <input
            type="text"
            value={newWorkflowItem}
            onChange={(e) => setNewWorkflowItem(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-lg mb-2"
            placeholder="Add Workflow Item"
          />
          <button
            type="button"
            onClick={() => {
              setWorkflowItems([...workflowItems, newWorkflowItem]);
              setNewWorkflowItem("");
            }}
            className="bg-blue-600 text-white p-2 rounded-lg"
          >
            Add Item
          </button>
        </div>
      )}

      {type === "vendorSolution" && (
        <>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Vendor Type</label>
            <input
              type="text"
              value={vendorType}
              onChange={(e) => setVendorType(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-lg"
              placeholder="Enter vendor type"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Vendor Name</label>
            <input
              type="text"
              value={vendorName}
              onChange={(e) => setVendorName(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-lg"
              placeholder="Enter vendor name"
              required
            />
          </div>
        </>
      )}

      {type === "vehicle" && (
        <>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Year</label>
            <input
              type="number"
              value={vehicleYear}
              onChange={(e) => setVehicleYear(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-lg"
              placeholder="Enter vehicle year"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Make</label>
            <input
              type="text"
              value={vehicleMake}
              onChange={(e) => setVehicleMake(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-lg"
              placeholder="Enter vehicle make"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Model</label>
            <input
              type="text"
              value={vehicleModel}
              onChange={(e) => setVehicleModel(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-lg"
              placeholder="Enter vehicle model"
              required
            />
          </div>
        </>
      )}

      {error && <p className="text-red-500 mb-4">{error}</p>}

      <button
        type="submit"
        className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700"
      >
        Add System
      </button>
    </form>
  );
};

export default SystemForm;
