import React, { useEffect } from "react";

// Define the System interface
interface System {
  _id: string;
  type: string; // 'workflow', 'vendorSolution', 'vehicle'
  workflowItems?: string[]; // Workflow items (for 'workflow')
  vendorType?: string; // Vendor type (for 'vendorSolution')
  vendorName?: string; // Vendor name (for 'vendorSolution')
  year?: number; // Year of the vehicle (for 'vehicle')
  make?: string; // Make of the vehicle (for 'vehicle')
  model?: string; // Model of the vehicle (for 'vehicle')
}

// Props for SystemTable
interface SystemTableProps {
  systems: System[]; // Systems array to display
  onDeleteSystem: (systemId: string) => void; // Delete system handler
  onEditSystem: (systemId: string) => void; // Edit system handler
}

// Workflow Table
const WorkflowTable: React.FC<SystemTableProps> = ({
  systems,
  onDeleteSystem,
  onEditSystem,
}) => {
  // Case-insensitive filtering for "workflow"
  const workflowSystems = systems.filter(
    (system) => system.type.toLowerCase() === "workflow"
  );

  return (
    <>
      <h3 className="text-xl font-semibold mb-4">Workflows</h3>
      {workflowSystems.length > 0 ? (
        <table className="min-w-full table-auto bg-white shadow-lg">
          <thead>
            <tr>
              <th className="px-4 py-2">Workflow Items</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {workflowSystems.map((system) => (
              <tr key={system._id}>
                <td className="border px-4 py-2">
                  <ul>
                    {system.workflowItems?.map((item, index) => (
                      <li key={index}>{item}</li>
                    ))}
                  </ul>
                </td>
                <td className="border px-4 py-2">
                  <button
                    onClick={() => onEditSystem(system._id)}
                    className="bg-yellow-500 text-white px-4 py-1 rounded mr-2 hover:bg-yellow-600"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => onDeleteSystem(system._id)}
                    className="bg-red-500 text-white px-4 py-1 rounded hover:bg-red-600"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-gray-500">No workflows available.</p>
      )}
    </>
  );
};

// Vendor Solution Table
const VendorSolutionTable: React.FC<SystemTableProps> = ({
  systems,
  onDeleteSystem,
  onEditSystem,
}) => {
  // Case-insensitive filtering for "vendorSolution"
  const vendorSolutions = systems.filter(
    (system) => system.type.toLowerCase() === "vendorsolution"
  );

  return (
    <>
      <h3 className="text-xl font-semibold mb-4">Vendor Solutions</h3>
      {vendorSolutions.length > 0 ? (
        <table className="min-w-full table-auto bg-white shadow-lg">
          <thead>
            <tr>
              <th className="px-4 py-2">Vendor Name</th>
              <th className="px-4 py-2">Vendor Type</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {vendorSolutions.map((system) => (
              <tr key={system._id}>
                <td className="border px-4 py-2">{system.vendorName}</td>
                <td className="border px-4 py-2">{system.vendorType}</td>
                <td className="border px-4 py-2">
                  <button
                    onClick={() => onEditSystem(system._id)}
                    className="bg-yellow-500 text-white px-4 py-1 rounded mr-2 hover:bg-yellow-600"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => onDeleteSystem(system._id)}
                    className="bg-red-500 text-white px-4 py-1 rounded hover:bg-red-600"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-gray-500">No vendor solutions available.</p>
      )}
    </>
  );
};

// Vehicle Table
const VehicleTable: React.FC<SystemTableProps> = ({
  systems,
  onDeleteSystem,
  onEditSystem,
}) => {
  // Case-insensitive filtering for "vehicle"
  const vehicleSystems = systems.filter(
    (system) => system.type.toLowerCase() === "vehicle"
  );

  return (
    <>
      <h3 className="text-xl font-semibold mb-4">Vehicles</h3>
      {vehicleSystems.length > 0 ? (
        <table className="min-w-full table-auto bg-white shadow-lg">
          <thead>
            <tr>
              <th className="px-4 py-2">Year</th>
              <th className="px-4 py-2">Make</th>
              <th className="px-4 py-2">Model</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {vehicleSystems.map((system) => (
              <tr key={system._id}>
                <td className="border px-4 py-2">{system.year}</td>
                <td className="border px-4 py-2">{system.make}</td>
                <td className="border px-4 py-2">{system.model}</td>
                <td className="border px-4 py-2">
                  <button
                    onClick={() => onEditSystem(system._id)}
                    className="bg-yellow-500 text-white px-4 py-1 rounded mr-2 hover:bg-yellow-600"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => onDeleteSystem(system._id)}
                    className="bg-red-500 text-white px-4 py-1 rounded hover:bg-red-600"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-gray-500">No vehicles available.</p>
      )}
    </>
  );
};

// Main System Table
const SystemTable: React.FC<SystemTableProps> = (props) => {
  console.log("All systems passed to the table:", props.systems); // Debugging: log all systems passed to the component

  return (
    <div className="space-y-8">
      <WorkflowTable {...props} />
      <VendorSolutionTable {...props} />
      <VehicleTable {...props} />
    </div>
  );
};

export default SystemTable;
